new Vue({
	el: '#login',
	data(){
		return {
			showing: false,
			show: `<i class="fas fa-fw fa-eye"></i>`,
			hide: `<i class="fas fa-low-vision"></i>`
		}
	},

	methods: {
		historyBack(){
			window.history.back()
		},

		showPassword(){
			const password = document.querySelector('#password')
			if(password.type === "password"){
				password.type = "text"
				this.showing = true
			}else{
				password.type = "password"
				this.showing = false
			}			
		}
	}
})


new Vue({
	el: '#nav',

	mounted(){
		this.scrollNav()
	},

	methods: {
		scrollNav(){
			window.addEventListener('scroll', function(){
				let scrollPos = window.scrollY
				let nav = document.querySelector('.navbar-evoush')
				let isDesktop = window.matchMedia('only screen and (min-width: 992px)').matches;
	          // console.log(scrollPos)
		          if(!isDesktop){
		          	nav.classList.add('mobileNav')
		          	if(scrollPos > 150){
		          		nav.classList.remove('navbar-light')
		          		nav.classList.add('navbar-light')
		          		nav.classList.add('transition')
		          		if(scrollPos > 400){
		          			nav.classList.add('bg-white')
		          		}else{
		          			nav.classList.remove('bg-white')
		          			nav.classList.add('transparent-nav')
		          		}
		          	}else{
		          		nav.classList.remove('transparent-nav')
		          		nav.classList.remove('bg-white')
		          		nav.classList.remove('transition')
		          	}
		          }else{
		          	nav.classList.remove('mobileNav')
		          	if(scrollPos > 150){
		          		nav.classList.remove('transparent-nav')
		          		nav.classList.add('transition')
		          		if(scrollPos > 400){
		          			nav.classList.add('bg-dark')
		          		}else{
		          			nav.classList.remove('bg-dark')
		          			nav.classList.add('transparent-nav')
		          		}
		          	}else{
		          		nav.classList.remove('transparent-nav')
		          		nav.classList.remove('bg-dark')
		          		nav.classList.remove('transition')
		          	}
		          }
	      	})
		},

		searchInput(e){
			console.log(e.target.value)
		}
	}
})


